__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual ON of 046 triggers 047 ON",
    "parent": 0,
    "comment": "When Output-046 is physically turned on overnight, turn Output-047 on",
    "if": [
      { "(": 1 },
        { "==": { "device": "n005_output_46", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_output_47", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "This routine listens for a Change of Control (physical ON) on Output-046 and restricts it to the overnight window (22:00->06:00). When triggered it sends an ON (DON) command to Output-047. No numerical parameters are required, so DON is used.">
  }
}
__END_NUCORE_ROUTINE__
