__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion triggers sensor queries",
    "parent": 0,
    "comment": "Query sensors when motion detected at night",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"ZY194_380","control":"DON" } }
    ],
    "then": [
      { "device":"ZY194_118","command":"QUERY","parameters":[] },
      { "device":"ZY179_1","command":"QUERY","parameters":[] }
    ],
    "else": [],
    "reasoning": "When motion is reported by the ZY194_380 during the night window, query the multilevel and backyard sensors to capture temperature/luminance/UV state at the moment of motion."
  }
}
__END_NUCORE_ROUTINE__
