__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Remote DFON triggers queries to both ZY009 devices",
    "parent": 0,
    "comment": "When Mini Remote sends Fast On, query both ZY009 devices",
    "if": [
      { "==": { "device": "26 33 AB 8", "control": "DFON" } }
    ],
    "then": [
      { "device": "ZY009_252N002", "command": "QUERY", "parameters": [] },
      { "device": "ZY009_252N003", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC on the Mini Remote DFON to proactively query both ZY009 devices so their current states are refreshed immediately after the remote is pressed."
  }
}
__END_NUCORE_ROUTINE__
