__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night motion triggers power alarm query",
    "parent": 0,
    "comment": "Query power alarm when motion detected overnight",
    "if": [
      { "==": { "device": "ZY015_155", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY015_309", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the motion sensor reports On (COS) during the overnight window (22:00-06:00), query the power alarm to collect status/power diagnostics. Using a night time window limits queries to relevant periods."
  }
}
__END_NUCORE_ROUTINE__
