__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion: query Lake view camera",
    "parent": 0,
    "comment": "When motion is detected overnight, query the Lake view device",
    "if": [
      { "==": { "device": "n006_23595482_m", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n006_23595482_m", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "The routine triggers when the Lake view motion sensor emits a Motion (DON). The time window limits actions to overnight (22:00-06:00). The action queries the same device to refresh status or capture current state when motion occurs at night."
  }
}
__END_NUCORE_ROUTINE__
