__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday evening DFON sets fireplace backlight",
    "parent": 0,
    "comment": "Activate fireplace backlight when remote fast-on is pressed during weekday evenings",
    "if": [
      { "==": { "device": "A 18 B 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "14 38 C6 6", "command": "BL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] },
      { "device": "14 38 C6 6", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) on Gary RM and restrict by a weekly time window for weekday evenings. Then set the LR fireplace backlight to 50% using the BL command and persist with WDU. No action otherwise."
  }
}
__END_NUCORE_ROUTINE__
