__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Enable backlight on available remotes",
    "parent": 0,
    "comment": "Turn backlight to 80% at sunset for remotes that are responding",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "(": 1 },
        { "==": { "device": "A 18 B 3", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "A 18 B 4", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } },
      { ")": 1 }
    ],
    "then": [
      {
        "device": "A 18 B 3",
        "command": "BL",
        "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ]
      },
      {
        "device": "A 18 B 4",
        "command": "BL",
        "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ]
      }
    ],
    "else": [],
    "reasoning": "At sunset enable backlight to 80% only for remotes that report Responding=True (ERR=0) to avoid sending commands to offline devices. Applies to device 3 and 4 and uses their Backlight (BL) command with uom 51."
  }
}
__END_NUCORE_ROUTINE__
