__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "NEW WEST high temp evening -> Inputs Channel Up",
    "parent": 0,
    "comment": "Press Channel Up when NEW WEST forecast exceeds 85°F during evening hours.",
    "if": [
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { ">": { "device": "n001_f521773762312", "status": "ST", "value": 850, "uom": "17", "precision": 1 } }
    ],
    "then": [
      { "device": "n010_d_7", "command": "channel_up", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use an evening schedule AND a COS temperature threshold on the NEW WEST Ecobee (ST). Temperature values use precision=1 (value 850 = 85.0°F). Action uses Inputs device Channel Up which exists in the device's accepts list."
  }
}
__END_NUCORE_ROUTINE__
