__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday: Turn off faucet when overhead DFON or garage turns On",
    "parent": 0,
    "comment": "When overhead is fast-on or garage changes to On during weekday daytime, ensure faucet is off.",
    "if": [
      { "(": 1 },
        { "==": { "device": "3D 2C 79 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "4E 48 50 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "37 65 49 1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Combine a COC (DFON on the Kitchen Overhead) and a COS (Garage status becomes On) with a weekday time window. If either trigger occurs during the window, explicitly send Faucet Off to ensure it is not running. No parameters required."
  }
}
__END_NUCORE_ROUTINE__
