__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Keypad FastOn -> LivingRoom Night",
    "parent": 0,
    "comment": "When DFON on Basement keypads during night, set living room to 30% and RR=0.5s",
    "if": [
      { "(": 1 },
        { "==": { "device": "33 F 75 7", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "33 F 75 8", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "56 7E 9B 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 28, "uom": "25", "precision": 0 } ] },
      { "device": "56 7E 9B 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "Separate routine triggered on DFON from either basement keypad during the night window. First set Ramp Rate to the 0.5s enum (value 28, uom 25) then set output to 30% (DON param 30, uom 51)."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Keypad FastOn -> LivingRoom Weekday Day",
    "parent": 0,
    "comment": "When DFON on Basement keypads during weekdays 09:00-17:00 set living room to 80%",
    "if": [
      { "(": 1 },
        { "==": { "device": "33 F 75 7", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "33 F 75 8", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "56 7E 9B 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "Second routine handles DFON during weekday daytime window and sets Living Room ON to 80% using DON with a 0-100% parameter."
  }
}
__END_NUCORE_ROUTINE__*/
