__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weeknight keypad quick-on: set movie ramp and level",
    "parent": 0,
    "comment": "When Intermission or C is fast-on between 18:00-22:00 on weekdays, adjust Movie keypad ramp and on level.",
    "if": [
      { "(": 1 },
        { "==": { "device": "33 F 7F 2", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "33 F 7F 3", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      {
        "device": "33 F 7F 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 27, "uom": "25", "precision": 0 }
        ]
      },
      {
        "device": "33 F 7F 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 30, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "COC: DFON on either Intermission or C during an evening weekday window should quickly prepare the Movie keypad for viewing by shortening ramp (2.0s -> enum index 27) and setting a moderate on level (30%). Grouped COC triggers ensure either keypad initiates the routine during the scheduled window."
  }
}
__END_NUCORE_ROUTINE__
