__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening guest entry — Output-005 300s",
    "parent": 0,
    "comment": "Turn on Output-005 for 300s when a user code is entered at Front Entry during 17:00-22:00",
    "if": [
      { "from": { "time":"17:00","to":{ "time":"22:00","day":0 } } },
      { "and": 1 },
      { "==": { "device":"n005_keypad_3", "control":"GV10" } }
    ],
    "then": [
      { "device":"n005_output_5", "command":"DON_WTIME", "parameters":[ { "name":"n/a","value":300,"uom":"58","precision":0 } ] }
    ],
    "else": [],
    "reasoning": "Use the keypad 'User Code Entered' send event (GV10) as a COC trigger and restrict to 17:00-22:00; DON_WTIME with 300 seconds runs Output-005 for the requested interval."
  }
}
__END_NUCORE_ROUTINE__
