__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Turn on Output-007 and Output-008 when Output-009 is Off for 300s",
    "parent": 0,
    "comment": "At sunset, if Output-009 is off, turn on outputs 7 and 8 for 300 seconds.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n005_output_9", "status": "ST", "value": 0, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "n005_output_7", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] },
      { "device": "n005_output_8", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a sunset schedule plus a check that Output-009 is Off (enum=0). If true, use each device's Turn On For (DON_WTIME) with 300 seconds to ensure they auto-off after 5 minutes. No actions otherwise."
  }
}
__END_NUCORE_ROUTINE__
