__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Overnight manual On of Output-022 triggers Output-023 for 120s",
    "parent": 0,
    "comment": "When Output-022 is turned on manually overnight, pulse Output-023",
    "if": [
      { "(": 1 },
      { "==": { "device": "n005_output_22", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "n005_output_23", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "This routine triggers only on a physical On (COC) of Output-022 during the overnight window; it uses the Turn On For command to run Output-023 for 120 seconds."
  }
}
__END_NUCORE_ROUTINE__
