__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Auto-off Output-028 after manual on",
    "parent": 0,
    "comment": "When Output-028 is manually turned on, run Turn On For for 60s",
    "if": [
      { "==": { "device": "n005_output_28", "control": "DON" } }
    ],
    "then": [
      { "device": "n005_output_28", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 60, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger for the manual On (control = DON). When that occurs, send the device its Turn On For (DON_WTIME) command with 60 seconds so the output will turn off automatically after 60s."
  }
}
__END_NUCORE_ROUTINE__
