__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night manual-on auto-off for Output-040",
    "parent": 0,
    "comment": "When Output-040 is manually turned on at night, auto turn it on for 120 seconds",
    "if": [
      { "==": { "device": "n005_output_40", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_output_40", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (control DON) to detect a physical/manual turn-on of Output-040. Restrict to the nightly window (22:00-06:00). When triggered, issue Turn On For (DON_WTIME) with 120 seconds so the output auto-turns off after 2 minutes."
  }
}
__END_NUCORE_ROUTINE__
