__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Auto-off Output-046 after manual ON",
    "parent": 0,
    "comment": "Auto-off when manually turned on",
    "if": [
      { "==": { "device": "n005_output_46", "control": "DON" } }
    ],
    "then": [
      { "device": "n005_output_46", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Detect physical ON (COC DON) on Output-046 and send Turn On For with 120 seconds so the output auto-turns off after 2 minutes. Uses the device's DON_WTIME command and the parameter UOM 58 (seconds)."
  }
}
__END_NUCORE_ROUTINE__
