__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Foyer on at sunset if not raining",
    "parent": 0,
    "comment": "Enable foyer lamp at sunset only when precipitation (mm) < 1",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "<": { "device": "n002_controller", "status": "PRECIP", "value": 1, "uom": "82", "precision": 2 } }
    ],
    "then": [
      { "device": "35 21 D6 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "At sunset, check AERIS Precipitation (PRECIP, mm). If <1 mm, send DON to Foyer Lamp with level 80% to turn it on at the desired brightness."
  }
}
__END_NUCORE_ROUTINE__
