__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "XMAS On at Sunset: set backlight and beep",
    "parent": 0,
    "comment": "When XMAS Module changes to On at sunset, set backlight to 60% and beep.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "40 B BF 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "40 B BF 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "40 B BF 1", "command": "BEEP" }
    ],
    "else": [],
    "reasoning": "Use a sunset schedule combined with a COS on the XMAS Module status (ST == On). When the module turns on at sunset, set Backlight (BL) to 60% using the defined range UOM 51, then send BEEP to announce. No action otherwise."
  }
}
__END_NUCORE_ROUTINE__
