__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Alert when garage wet sensor turns on",
    "parent": 0,
    "comment": "Query both leak sensors and beep the heart when Wet sensor reports On (COS)",
    "if": [
      { "==": { "device":"2D D3 8D 2","status":"ST","value":100,"uom":"51","precision":0 } }
    ],
    "then": [
      { "device":"2D D3 8D 2","command":"QUERY" },
      { "device":"2D D3 8D 4","command":"QUERY" },
      { "device":"2D D3 8D 4","command":"BEEP","parameters":[] }
    ],
    "else": [],
    "reasoning": "This routine triggers on a COS when Leak.Garage.Wet status becomes On (value 100). Immediate queries refresh both leak devices' states and a BEEP on the Heart alerts occupants. BEEP is invoked without parameters as none are specified in the DEVICE STRUCTURE for required values."
  }
}
__END_NUCORE_ROUTINE__
