__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Start pump when outlet turns ON during weekday mornings",
    "parent": 0,
    "comment": "Start Water Heater Pump when Water Heater Outlet COS to On between 06:00 and 08:00 on weekdays.",
    "if": [
      { "weekly": { "days":"mon,tue,wed,thu,fri", "from":{ "time":"06:00", "to":{ "time":"08:00" } } } },
      { "and": 1 },
      { "==": { "device":"46 BF B1 1", "status":"ST", "value":100, "uom":"51", "precision":0 } }
    ],
    "then": [
      { "device":"46 BF B1 2", "command":"DON" }
    ],
    "else": [],
    "reasoning": "During weekday mornings (06:00-08:00) if the Water Heater Outlet changes state to On (COS to value 100 uom 51), start the Water Heater Pump via DON. No action outside the condition.">
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Stop pump when outlet turns OFF",
    "parent": 0,
    "comment": "Stop Water Heater Pump whenever the Water Heater Outlet COS to Off.",
    "if": [
      { "==": { "device":"46 BF B1 1", "status":"ST", "value":0, "uom":"51", "precision":0 } }
    ],
    "then": [
      { "device":"46 BF B1 2", "command":"DOF" }
    ],
    "else": [],
    "reasoning": "When the Water Heater Outlet changes state to Off (COS to value 0 uom 51), immediately stop the Water Heater Pump with DOF to ensure safety and avoid dry run."
  }
}
__END_NUCORE_ROUTINE__
