__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Nighttime front-door open beeps bedroom",
    "parent": 0,
    "comment": "Beep Bedroom Door Outside.Hear when Front Door opens overnight",
    "if": [
      { "(": 1 },
        { "==": { "device": "25 BD 27 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "28 F A7 4", "command": "BEEP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Front Door Sensor reports On (open) during overnight hours (22:00-06:00), send the BEEP command to Bedroom Door Outside.Hear which accepts BEEP. This uses a status COS (front door ST==100) combined with a nightly schedule."
  }
}
__END_NUCORE_ROUTINE__
