__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "On wake set 3600s and enable Keep Awake",
    "parent": 0,
    "comment": "When ZY015_199 reports Is Awake = True, set interval and enable Keep Awake",
    "if": [
      { "==": { "device": "ZY015_199", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY015_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 3600, "uom": "58", "precision": 0 } ] },
      { "device": "ZY015_199", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on the Wake Up node reporting Is Awake == True (COS). Use SETIV with uom 58 (seconds) to set 3600s, then send DON to enable Keep Awake so the device remains awake after waking."
  }
}
__END_NUCORE_ROUTINE__
