__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 201,
    "name": "Scene Button 1 Fast On -> Turn Shop Front Overhead On",
    "parent": 0,
    "comment": "Turn on overhead when Button 1 fast on is pressed",
    "if": [
      { "==": { "device": "ZY008_201N001", "control": "DFON" } }
    ],
    "then": [
      { "device": "ZY008_1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "A COC (Fast On) from Scene Button 1 is the explicit user control event; respond immediately by sending the DON command to the Shop Front Overhead device which accepts DON. No parameters required."
  }
}
__END_NUCORE_ROUTINE__
