__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Start Lawn Fig side on manual start during morning window",
    "parent": 0,
    "comment": "Starts Lawn Fig side for 30 minutes when manually started between 06:00 and 07:00 and zone enabled",
    "if": [
      { "==": { "device": "n003_083a88cc667e14", "control": "START" } },
      { "and": 1 },
      { "from": { "time": "06:00", "to": { "time": "07:00", "day": 0 } } },
      { "and": 1 },
      { "==": { "device": "n003_083a88cc667e14", "status": "GV0", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n003_083a88cc667e14", "command": "START", "parameters": [ { "name": "n/a", "value": 30, "uom": "45", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on a physical Start control (COC) for the Lawn Fig side device during the 06:00-07:00 daily window; require the zone Enabled property (GV0 == True). Use the device's Start command with its defined minutes parameter set to 30 (uom 45, precision 1)."
  }
}
__END_NUCORE_ROUTINE__
