__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Power on Zone2 if AVR Online",
    "parent": 0,
    "comment": "Enable Zone2 at sunset only when AVR reports Online",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n010_d_12", "status": "ST", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n010_d_13", "command": "power" }
    ],
    "else": [],
    "reasoning": "Run at sunset but only when ONKYO_TX_RZ830 reports Onkyo AVR Online (ST=1). This avoids powering Zone2 when the AVR is offline."
  }
}
__END_NUCORE_ROUTINE__
