__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Output-010 COS ON -> Turn Output-011 on for 300s",
    "parent": 0,
    "comment": "When Output-010 changes to ON, pulse Output-011 for 300 seconds",
    "if": [
      { "==": { "device":"n005_output_10","status":"ST","value":100,"uom":"78","precision":0 } }
    ],
    "then": [
      { "device":"n005_output_11","command":"DON_WTIME","parameters":[{ "name":"n/a","value":300,"uom":"58","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Listen for a COS on Output-010 to ON (status ST == 100). When triggered, send Turn On For (DON_WTIME) to Output-011 for 300 seconds to ensure a timed activation. No else actions required."
  }
}
__END_NUCORE_ROUTINE__
