__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Switch ON -> Lamp level 200 ramp30",
    "parent": 0,
    "comment": "Triggered by physical On sent from ZB21110_003_1",
    "if": [
      { "==": { "device": "ZB21110_003_1", "control": "DON" } }
    ],
    "then": [
      {
        "device": "ZB31656_001_1",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 200, "uom": "100", "precision": 0 },
          { "name": "Ramp Rate", "value": 30, "uom": "58", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use a COC (On control) from the physical switch (ZB21110_003_1) to immediately set the lamp to a specific numeric level (use uom 100 for Level) and supply ramp in seconds (uom 58). Separate routine handles OFF event."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Switch OFF -> Lamp off",
    "parent": 0,
    "comment": "Triggered by physical Off sent from ZB21110_003_1",
    "if": [
      { "==": { "device": "ZB21110_003_1", "control": "DOF" } }
    ],
    "then": [
      {
        "device": "ZB31656_001_1",
        "command": "DOF",
        "parameters": []
      }
    ],
    "else": [],
    "reasoning": "When the physical switch sends Off, issue the lamp Off command to ensure the light is turned off."
  }
}
__END_NUCORE_ROUTINE__
