__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday: E fast-on or H becomes On -> set G On",
    "parent": 0,
    "comment": "When MiniRemote - E is fast-on OR MiniRemote - H switches to On during weekdays 08:00-18:00, set MiniRemote - G to On.",
    "if": [
      { "(": 1 },
      { "==": { "device":"3F 0 2B 6", "control":"DFON" } },
      { "or": 1 },
      { ">=": { "device":"3F 0 2B 7", "status":"ST", "value":100, "uom":"51", "precision":0 } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days":"mon,tue,wed,thu,fri", "from": { "time":"08:00", "to": { "time":"18:00" } } } }
    ],
    "then": [
      { "device":"3F 0 2B 8", "command":"WDU", "parameters": [ { "name":"ST", "value":100, "uom":"51", "precision":0 } ] }
    ],
    "else": [],
    "reasoning": "Limit to weekdays daytime to avoid accidental activation; actions set MiniRemote - G status to On via Write Changes."
  }
}
__END_NUCORE_ROUTINE__
