__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night fast-on: Sunroom DFON turns on lamp & pond",
    "parent": 0,
    "comment": "Triggered by a physical fast-on of the Sunroom outlet during night hours",
    "if": [
      { "==": { "device": "51 28 A4 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "21:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "3A 2E B7 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "FF 0F 0F 1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "This routine uses a COC (DFON) from the Sunroom Outlet and limits the action to night hours (21:00-06:00). When triggered, it sets the Nightstand Lamp to a defined on-level (30%) using DON with a percent parameter and ensures the Lower Pond Outlet is turned on. The else branch is empty because no alternate action was requested."
  }
}
__END_NUCORE_ROUTINE__
