__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Double-tap kitchen pad: set scene",
    "parent": 0,
    "comment": "Weekday double-tap configures on-level, ramp and LED",
    "if": [
      { "==": { "device": "61 8C 6D 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "61 8C 6D 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "61 8C 6D 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "61 8C 6D 1", "command": "LED", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger on the Kitchen Under I3 Padd DFON with tapCount=2 during weekday business hours. Then set On Level to 80% (OL parameter 80), choose Ramp Rate index 12 for 1.0 minute, and set LED to On (value 1) using the device's accepted commands and parameter UOMs."
  }
}
__END_NUCORE_ROUTINE__
