{
  "routine": {
    "id": 1,
    "name": "Sunset - ensure Sonos audible when playing",
    "parent": 0,
    "comment": "Unmute and set volume at sunset only if already playing",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n001_43875cc0b8e014", "status": "ST", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "n001_43875cc0b8e014", "command": "UNMUTE", "parameters": [] },
      { "device": "n001_43875cc0b8e014", "command": "VOLUME", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger at sunset and only act if the Sonos is currently Playing (ST == 0). First ensure it's unmuted, then set volume to 30% using the device's VOLUME command parameter (uom 51)."
  }
}
__END_NUCORE_ROUTINE__
