__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday GPV high -> Query ZY002_118",
    "parent": 0,
    "comment": "Query multilevel sensor when GPV > 100 during business hours",
    "if": [
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } },
      { "and": 1 },
      { ">": { "device": "ZY002_118", "status": "GPV", "value": 100, "precision": 0 } }
    ],
    "then": [
      { "device": "ZY002_118", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Limit to weekdays 09:00-17:00 so queries only run during business hours. The COS condition checks GPV > 100; when true the routine sends QUERY to ZY002_118 to refresh its state. QUERY requires no parameters."
  }
}
__END_NUCORE_ROUTINE__
