__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Night: turn laundry fan on when closet fast-on",
    "parent": 0,
    "comment": "When Master_closet_left receives DFON between 22:00-06:00, start Laundry_fan",
    "if": [
      { "==": { "device": "43 57 55 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"43 57 73 1","command":"DON","parameters":[] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on Master_closet_left during the defined night window to trigger the Laundry_fan ON; keeps routines simple and responsive to physical control."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1002,
    "name": "Night: turn laundry fan off when closet fast-off",
    "parent": 0,
    "comment": "When Master_closet_left receives DFOF between 22:00-06:00, stop Laundry_fan",
    "if": [
      { "==": { "device": "43 57 55 1", "control": "DFOF" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"43 57 73 1","command":"DOF","parameters":[] }
    ],
    "else": [],
    "reasoning": "Mirror routine for fast-off (DFOF) to ensure the fan is turned off when closet is fast-switched off at night."
  }
}
__END_NUCORE_ROUTINE__
