__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Triple-press Scene Button 1 -> Query Scene Button 2",
    "parent": 0,
    "comment": "When Scene Button 1 is triple-pressed, request a query of Scene Button 2",
    "if": [
      { "==": { "device": "ZY009_201N001", "control": "DON3" } }
    ],
    "then": [
      { "device": "ZY009_201N002", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (triple press) from Scene Button 1 (it sends DON3). The action must use the target device's accepted command (QUERY on ZY009_201N002). No parameters are required."
  }
}
__END_NUCORE_ROUTINE__
