__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Close garage at sunset if open",
    "parent": 0,
    "comment": "Close ZY013_1 at sunset when open",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY013_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY013_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "At sunset, if the garage door reports On (100 = open), send DOF to close it and secure the garage."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Open on physical remote press during evening window",
    "parent": 0,
    "comment": "If either assoc device sends DON between 18:00 and 22:00, ensure garage door opens",
    "if": [
      { "from": { "time": "18:00", "to": { "time": "22:00", "day": 0 } } },
      { "and": 1 },
      { "(": 1 },
        { "==": { "device": "ZY013_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY013_252N003", "control": "DON" } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY013_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "During 18:00-22:00, if a physical remote (assoc 2 or 3) is pressed (sends DON), open the main garage (DON) to honor manual control and keep it accessible."
  }
}
__END_NUCORE_ROUTINE__
