__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 301,
    "name": "Sunset turn ON if responding - Shop Front Overhead",
    "parent": 0,
    "comment": "Turn overhead on at sunset only when device responds",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY008_1", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY008_1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Enable lights at sunset only when the device reports Responding=True to avoid sending commands to an offline device. Uses ERR (uom 25) equality check and the exact sunset schedule."
  }
}
__END_NUCORE_ROUTINE__
