__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Triple press => Green ON",
    "parent": 0,
    "comment": "Triple-press (DON3) on ZY059_201N001 turns ZY006_002_1 to On (100)",
    "if": [
      { "==": { "device": "ZY059_201N001", "control": "DON3" } }
    ],
    "then": [
      { "device": "ZY006_002_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use the scene button's Triple Press send command (DON3) as a Change of Control trigger to send the Green device its On command with parameter value 100 (uom 78)."
  }
}
__END_NUCORE_ROUTINE__
