__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "When Yellow manually turned on on weekdays, set WinkSE param 10 to 5 and write",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY006_005_1", "control": "DON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } },
      { ")": 1 }
    ],
    "then": [
      {
        "device": "ZY043_1",
        "command": "CONFIG",
        "parameters": [
          { "name": "Parameter Number", "value": 10, "precision": 0 },
          { "name": "Parameter Value", "value": 5, "uom": "107", "precision": 0 }
        ]
      },
      {
        "device": "ZY043_1",
        "command": "WDU",
        "parameters": []
      }
    ],
    "else": [],
    "reasoning": "Use a Change of Control (user pressed Yellow ON) limited to weekday business hours; then set WinkSE Parameter Number (NUM) to 10 and Parameter Value (VAL) to 5 using 1-byte unsigned (uom 107), followed by Write Changes to persist the configuration."
  }
}
__END_NUCORE_ROUTINE__
