__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overheat On -> Query MBR, set param 5 = 1, write changes",
    "parent": 0,
    "comment": "Respond to Overheat COS",
    "if": [
      { "==": { "device": "ZY054_166", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY054_1", "command": "QUERY", "parameters": [] },
      { "device": "ZY054_1", "command": "CONFIG", "parameters": [ { "name": "NUM", "value": 5, "precision": 0 }, { "name": "VAL", "value": 1, "uom": "107", "precision": 0 } ] },
      { "device": "ZY054_1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on a COS where the Overheat child (ZY054_166) becomes On (100). Query the parent to refresh state, set parameter number 5 to 1 using the 1-byte unsigned UOM (107) via CONFIG, then persist with WDU. This uses only commands accepted by the parent device."
  }
}
__END_NUCORE_ROUTINE__
