__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Alarm not responding -> keep awake 120min",
    "parent": 0,
    "comment": "When Responding changes to False ensure the wake child stays awake and has a 120 minute interval.",
    "if": [
      { "==": { "device": "ZY041_1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY041_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY041_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 120, "uom": "45", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A COS on the Alarm's Responding (ERR) equals 1 indicates it stopped responding. Immediately send Keep Awake (DON) to the Wake Up child and set its Wake Interval to 120 minutes (uom 45 = minutes) so the node will remain awake for retries and configuration.">
  }
}
__END_NUCORE_ROUTINE__
