__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual keep-awake sets 2h interval",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY045_199", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY045_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 2, "uom": "20", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "If the Wake Up device receives a manual Keep Awake control during the night window, shorten its wake interval to 2 hours so the device remains responsive overnight. Uses COC (DON) plus a night schedule to limit scope."
  }
}
__END_NUCORE_ROUTINE__
