__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Evening remote DFON turns on fountain",
    "parent": 0,
    "comment": "Pressing Guest RM fast-on during evening turns fountain on and records remote change",
    "if": [
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { "==": { "device": "54 E9 8A 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "51 C 8E 1", "command": "DON", "parameters": [] },
      { "device": "54 E9 8A 1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a schedule window for evening and a COC (DFON) from the Guest RM. When the remote fast-on is pressed between 18:00-23:00, send DON to the fountain to turn it on and WDU to the remote to persist any remote state changes."
  }
}
__END_NUCORE_ROUTINE__
