__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Side gate lights on at sunset when motion detected",
    "parent": 0,
    "comment": "Turn on side gate lights when motion occurs at sunset",
    "if": [
      { "==": { "device": "n006_18328395_m", "control": "DON" } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "n006_18328395_lt", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the Side Gate motion sensor emits a motion event (DON) at sunset, the routine turns on the Side Gate lights using the lights' DON command. No else action is required."
  }
}
__END_NUCORE_ROUTINE__
