__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening plants: Niche DFON -> set coffee & kitchen backlights",
    "parent": 0,
    "comment": "DFON on LR: Blk Remote Niche between 18:00-23:00 sets backlights to 80% and writes changes",
    "if": [
      { "==": { "device": "14 38 C6 3", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "14 38 C6 4", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "14 38 C6 4", "command": "WDU", "parameters": [] },
      { "device": "14 38 C6 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "14 38 C6 5", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "The user requested a manual button press (COC DFON) to set both plant backlights only during the evening window; each BL command uses the BL accept parameter (0-100 % uom 51) and WDU commits the changes."
  }
}
__END_NUCORE_ROUTINE__
