__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night button DFON sets KP-C backlight low",
    "parent": 0,
    "comment": "When LITTLE FAN DFON pressed overnight set KP-C backlight to 10",
    "if": [
      { "==": { "device":"58 14 C0 2","control":"DFON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"4D 33 F5 5","command":"BL","parameters":[ { "name":"n/a","value":10,"uom":"25","precision":0 } ] },
      { "device":"4D 33 F5 5","command":"WDU","parameters":[] }
    ],
    "else": [],
    "reasoning": "Use COC DFON from the Media RM button restricted to the overnight window so the backlight is only lowered at night; BL sets the backlight index (uom 25) and WDU commits the change."
  }
}
__END_NUCORE_ROUTINE__
