__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Game Closet DFON -> MBR on + backlight 50",
    "parent": 0,
    "comment": "Respond to physical fast-on of Game Closet Light",
    "if": [
      { "==": { "device": "54 A6 DD 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "54 A5 5F 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "54 A5 5F 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use the COC (DFON) from the Game Closet Light (device 54 A6 DD 1) as a trigger; immediately send DON to the undersink lamp and then set its backlight to 50% using BL. Parameters use the device-defined uom 51 and integer precision 0."
  }
}
__END_NUCORE_ROUTINE__
