__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Remote FastOn -> Gary set 75% with 1min ramp",
    "parent": 0,
    "comment": "Trigger from MBR remote DFON to set Gary RM fan-light level and ramp rate",
    "if": [
      { "==": { "device": "1C 44 CB 2", "control": "DFON" } }
    ],
    "then": [
      { "device": "14 8C 36 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 75, "uom": "51", "precision": 0 } ] },
      { "device": "14 8C 36 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "User requested a direct control action when the MBR remote Fast On (DFON) is pressed. The DFON send from device 1C 44 CB 2 is a COC trigger. The routine issues a DON command to Gary (14 8C 36 1) with 75% (uom 51) and sets Ramp Rate via RR to the enumerated value 12 (1.0 minute, uom 25)."
  }
}
__END_NUCORE_ROUTINE__
