__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday workout start: fan on + pad backlight",
    "parent": 0,
    "comment": "Trigger when workout fan Fast On pressed during weekday mornings",
    "if": [
      { "==": { "device": "4C 74 98 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "08:00" } } } }
    ],
    "then": [
      { "device": "4C 74 98 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "4C 74 98 3", "command": "BL", "parameters": [ { "name": "n/a", "value": 26, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on the workout fan plus a weekly time window to limit to weekday mornings. Then set the fan On (DON) to 80% and set the child unit A backlight (BL) to index 26. This uses only commands and UOMs defined in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
