__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night DFON -> motor low + kpd1 25%",
    "parent": 0,
    "comment": "Fast on (DFON) on Kpd.A during overnight should put motor and keypad to low level",
    "if": [
      { "==": { "device": "4C 74 97 3", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "47 7F CE 2", "command": "DON", "parameters": [ { "name": "n/a", "value": 25, "uom": "51", "precision": 0 } ] },
      { "device": "4C 74 97 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 25, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from keypad A limited to the overnight schedule (22:00->06:00 day rollover). Then send DON with parameter 25 (Low) to the motor and DON 25 to keypad 1 to set its level to 25%.">
  }
}
__END_NUCORE_ROUTINE__
